/**
 * Parse the DCS BIOS export message from the UDP client.
 * Apply updates to the retained buffer in flow context.
 * Transform msg.payload into an array of modified addresses.
 */

// the original message payload, binary buffer from dcs-bios
let buf = msg.payload;
// msg.payload transforms into an array of updated addresses
msg.payload = flow.get("updateAddresses") || [];
msg.payload.length = 0;

/**
 * Get (or create) the stored output buffer that we will apply updates to.
 * RawData is stored in binary format.
 */
let rawData = flow.get("rawData") || Buffer.alloc(65536);

// check for valid message format, the first 4 bytes are always 0x55
if (buf.length < 4 ||
    buf.readUInt32LE(0) !== 0x55555555)
{
    node.error("Malformed DCS-BIOS message received!", msg);
    return;
}

let offset = 4;

// Go through each update block in the message.
while (buf.length - offset > 0) {
    let startAddress = buf.readUInt16LE(offset);
    offset += 2;

    let dataLenB = buf.readUInt16LE(offset);
    offset += 2;

    buf.copy(rawData, startAddress, offset, offset + dataLenB);
    offset += dataLenB;

    msg.payload.push({ startAddress, dataLenB });
}

flow.set("rawData", rawData);
flow.set("updateAddresses", msg.payload);

return msg;

/**
 * Merges multiple module config objects into one.
 * Flattens the input structure to flow.controls object.
 * Sets msg.payload to flow.controls object.
 * Passes the message after 4 source messages are received.
 */

let count = context.get("count") || 0;

let controls = flow.get("controls") || {};

// flatten the config object into the controls collection
Object.values(msg.payload).reduce(
    (accum, group) => {
        return Object.assign(accum, group);
    }, controls);

// keep count of config objects we've processed
++count;
context.set("count", count);

flow.set("controls", controls);

if (count >= 4) {
    msg.payload = controls;
    return msg;
}

return null;

/**
 * Pivots the flow.outputs object to a Map indexed by output address.
 * This is convenient for quickly updating based on the output data stream.
 * Sets msg.payload to the new Map object, flow.outputAddressMap.
 */

let outputs = flow.get("outputs");

let addressMap = Object.values(outputs).reduce(
    (map, output) => {
        let addr = output.address;

        // map multiple controls per address, since many controls are bitfields
        // and share the same base address
        let outputList = map.get(addr) || [];
        outputList.push(output);

        map.set(addr, outputList);

        return map;
    }, new Map());

flow.set("outputAddressMap", addressMap);
msg.payload = addressMap;

return msg;

/**
 * Converts the array of update addresses to the corresponding set of
 * output control addresses.
 * Integer data updates yield the same address, while partial
 * string updates must step backward until the nearest control base
 * address is found.
 * 
 * Updated values are deserialized and written to the output
 * object's val property.
 * 
 * The returned msg.payload is an array of outputs to trigger change events.
 */

let addressMap = flow.get("outputAddressMap");
let rawData = flow.get("rawData");
let now = Date.now();
let changedOutputs = [];

msg.payload.forEach(({ startAddress, dataLenB }) => {
    // do some asserts to check for errors, remove later if they are never encountered
    if (Math.abs(startAddress % 2) === 1 || Math.abs(dataLenB % 2) === 1 || dataLenB === 0) {
        node.error(`Unexpected update address (${startAddress}) or length (${dataLenB})`);
    }

    // start at the end of the update data stream to walk backwards, possibly through multiple controls
    // Note: subtract 2 instead of 1 since we want even addresses, and dataLenB is always even
    let controlAddr = startAddress + dataLenB - 2;

    // make sure we find all controls back to the start of the data stream
    while (controlAddr >= startAddress) {
        // if not at a base control address, step backward to find nearest control
        while (!addressMap.has(controlAddr)) {
            controlAddr -= 2;
        }

        let outputsAtAddress = addressMap.get(controlAddr);
        outputsAtAddress.forEach((output) => {

            let hasChanged = false;

            // if this is an integer
            switch (output.type.charAt(0)) {
                case "i": {
                    output.prevVal = output.val;
                    let intVal = rawData.readUInt16LE(controlAddr);
                    // get the bits representing this value using mask and shift_by
                    output.val = (intVal & output.mask) >> output.shift_by;
                    hasChanged = (output.val !== output.prevVal);
                    break;
                }
                case "s": {
                    //output.prevVal = output.rawVal;
                    output.prevVal = output.hexVal;
                    output.val = rawData.toString("utf8", controlAddr, controlAddr + output.max_length);
                    //output.asciiVal = rawData.toString("ascii", controlAddr, controlAddr + output.max_length);
                    output.hexVal = rawData.toString("hex", controlAddr, controlAddr + output.max_length);
                    output.rawVal = rawData.slice(controlAddr, controlAddr + output.max_length);
                    //hasChanged = !output.rawVal.equals(output.prevVal);
                    hasChanged = (output.hexVal !== output.prevVal);
                    break;
                }
                default:
                    node.warn("unknown output type: " + output.type);
            }

            // add the output to trigger a change event downstream, prevent duplicates
            if ((hasChanged ||
                now - output.lastUpdate >= 1000) &&
                !changedOutputs.some((o) => Object.is(o, output)))
            {
                changedOutputs.push(output);
            }

            // capture update time for telemetry
            output.updateDelta = now - output.lastUpdate;
            output.lastUpdate = now;
        });

        controlAddr -= 2;
    }
});

msg.payload = changedOutputs;
return msg;

// attempt to throttle the data pump if we reach spiral-of-death conditions
msg.frame = flow.get("nextFrame") || 1;
if (msg.frame !== (flow.get("processedFrame") || 0) + 1) {
    // dropping frames is not desirable, so log a warning if it happens, try to optimize
    node.warn("frame dropped msg.frame=" + msg.frame + ", processedFrame=" + flow.get("processedFrame"));
    return;
}
flow.set("nextFrame", msg.frame + 1);
return msg;

let outputs = flow.get("outputs");

if (!outputs.hasOwnProperty(msg.payload)) {
    msg.payload = [];
    msg.val = "";
    msg.description = "";
    msg.output = null;
}
else {
    let output = outputs[msg.payload];
    let now = Date.now();

    let timeSinceLastUpdate = now - output.lastUpdate;
    msg.payload = 1000.0 / (output.updateDelta + timeSinceLastUpdate);
    msg.val = output.val;
    msg.description = output.control.description + " " + output.description + "(Address: " + output.address + ")";
    msg.output = output;
}

return msg;

/**
 * Flattens the flow.controls object further to a
 * flow.outputs object.
 * Sets msg.payload to flow.outputs object.
 */

let oldOutputs = flow.get("outputs");

let outputs = Object.values(msg.payload).reduce(
    (accum, control) => {
        control.outputs.forEach((output, idx) => {
            let outputName = control.identifier + output.suffix;

            // Copy values from the existing object being overwritten, so the pit
            // doesn't reset everything when we inject new configs mid-session.
            let oldOutput = oldOutputs && oldOutputs[outputName];

            let newOutput = {
                fullName: outputName,
                control: control,
                outputIndex: idx,
                val: (oldOutput ? oldOutput.val : null),
                prevVal: (oldOutput ? oldOutput.prevVal : null)
            };

            Object.assign(newOutput, control, output);
            delete newOutput.inputs;
            delete newOutput.outputs;

            accum[outputName] = newOutput;
        });
        return accum;
    }, {});

flow.set("outputs", outputs);

msg.payload = outputs;
return msg;

let outputs = flow.get("outputs");
msg.options = [{ "- Disabled -": "off" }];

for (let name in outputs) {
    msg.options.push(name);
}

return msg;

let now = Date.now();

msg.payload.forEach((output) => {
    let topic = "dcs-bios/output/" +
                output.category.toLowerCase().split(' ').join('_') +
                "/" +
                output.fullName.toLowerCase();

    let qos = 0;
    let mqttPayload = null;
    if (output.type.charAt(0) === "i") {
        // 1-byte integer (0/1)
        if (output.max_value === 1) {
            mqttPayload = output.val;
            qos = 1;
        }
        // 2-byte integer
        else {
            mqttPayload = output.val;
        }
    }
    else {
        // string
        mqttPayload = output.val;
        qos = 1;
    }

    output.mqtt = {
        topic: topic,
        qos: qos,
        retain: false,
        payload: mqttPayload
    };

    output.lastDelivered = now;
    output.lastMQTTPayload = mqttPayload;
});

return msg;

/**
 * Filter the set of outputs by control_type.
 * Here are the different control_types in DCS:
 *  "selector"
 *  "analog_gauge"
 *  "limited_dial"
 *  "analog_dial"
 *  "display"
 *  "led"
 *  "variable_step_dial"
 *  "discrete_dial"
 *  "electrically_held_switch"
 *  "metadata"
 * 
 * In general, we only need to send analog_gauge,
 * display, led, and electrically_held_switch to the
 * boards, but sendIdentifiers and sendCategories
 * allow for inclusion of extra values that panels
 * typically need for lighting and mechanical features.
 * Edit these lists as needed.
 * 
 * Also implements a rate limit for analog_gauges to
 * avoid overloading the MCUs with a pointlessly high
 * update rate.
 */

// include outputs by "control_type"
const sendTypes = [
    "analog_gauge",
    "display",
    "led",
    "electrically_held_switch"//,
    //"metadata"
    ];
// include outputs by "identifier"
const sendIdentifiers = [
    "LCP_ACCEL_COMP","LCP_AUX_INST","LCP_CONSOLE","LCP_ENG_INST","LCP_FLIGHT_INST","LCP_FLOOD",
    "CDU_BRT",
    "CMSC_BRT",
    "CMSP_BRT",
    "RWR_BRT",
    "EPP_EMER_FLOOD",
    "ENVCP_AIR_SUPPLY","ENVCP_FLOW_LEVEL","ENVCP_TEMP_LEVEL","ENVCP_AC_OPER",
    "DOWNLOCK_OVERRIDE",
    "AUX_GEAR_LOCK",
    "LMFD_PWR","RMFD_PWR",
    "_ACFT_NAME"
    ];
// include outputs by "category"
const sendCategories = [
    "Circuit Breaker Panel"//,
    //"Metadata"
    ];

const now = Date.now();
const CHANGE_1_DEG = 65535 / 360;
const MILLIS_BTWN_MSG = 100;

msg.payload = msg.payload.filter((output) => {

    let send =
        // filter by send types
        (sendTypes.includes(output.control_type) ||
         sendIdentifiers.includes(output.identifier) ||
         sendCategories.includes(output.category)) &&
        // limit the outgoing messages for analog_gauges
        (output.control_type !== "analog_gauge" ||
         // unless the change is large enough to result in a needle movement of 3 degree (on a 360 gauge)
         (Math.abs(output.val - output.prevVal) >= CHANGE_1_DEG) ||
         // or the value is pegged at stops (to prevent "stuck" needle just inside of stops)
         (output.val === 0 || output.val === 65535) ||
         // or otherwise limit the analog_gauge message rate, dropping intermittent messages
         (now - (output.lastDelivered || 0) >= MILLIS_BTWN_MSG));

    return send;
})

return msg;

// for the CDU, send raw bytes instead of the
// UTF-8 string so we can handle special characters
msg.payload.mqtt.payload = msg.payload.rawVal;

return msg.payload.mqtt;

// starting at address 4592 for CDU_LINE2
//msg.payload = msg.payload.slice(4592);
return msg;

let ctrl = msg.topic.substr(msg.topic.lastIndexOf("/")+1).toUpperCase();
msg.payload = `${ctrl} ${msg.payload}\n`;
return msg;

var result = [ null, null ];

if (msg.payload.control_type === "limited_dial") {
    result[0] = msg;
}
else {
    result[1] = msg.payload.mqtt;
}

return result;

// map value range to 0-1023 for pwm on arduino
msg.payload.mqtt.payload = Math.round(msg.payload.val / msg.payload.max_value * 1023);

return msg.payload.mqtt;

return {
    topic: "dcs-bios/goodbye",
    payload: "goodbye",
    qos: 1
};
